import React, { useEffect, useRef } from 'react';
import './Background.css';

const Background = () => {
    const canvasRef = useRef(null);

    useEffect(() => {
        const canvas = canvasRef.current;
        const ctx = canvas.getContext('2d');
        let animationFrameId;

        // Set canvas size
        const resizeCanvas = () => {
            canvas.width = window.innerWidth;
            canvas.height = window.innerHeight;
        };
        resizeCanvas();
        window.addEventListener('resize', resizeCanvas);

        // Particle class
        class Particle {
            constructor() {
                this.x = Math.random() * canvas.width;
                this.y = Math.random() * canvas.height;
                this.z = Math.random() * 1000;
                this.size = Math.random() * 3 + 1;
                this.speedX = (Math.random() - 0.5) * 0.3;
                this.speedY = (Math.random() - 0.5) * 0.3;
                this.speedZ = (Math.random() - 0.5) * 0.2;
                // Neon colors: cyan, magenta, and electric blue
                const colors = [
                    'hsla(180, 100%, 50%, 0.8)',  // Cyan
                    'hsla(300, 100%, 50%, 0.8)',  // Magenta
                    'hsla(240, 100%, 50%, 0.8)'   // Electric Blue
                ];
                this.color = colors[Math.floor(Math.random() * colors.length)];
            }

            update() {
                this.x += this.speedX;
                this.y += this.speedY;
                this.z += this.speedZ;

                if (this.x < 0 || this.x > canvas.width) this.speedX *= -1;
                if (this.y < 0 || this.y > canvas.height) this.speedY *= -1;
                if (this.z < 0 || this.z > 1000) this.speedZ *= -1;
            }

            draw() {
                const scale = 1000 / (1000 + this.z);
                const x = (this.x - canvas.width / 2) * scale + canvas.width / 2;
                const y = (this.y - canvas.height / 2) * scale + canvas.height / 2;
                const size = this.size * scale;

                ctx.beginPath();
                ctx.arc(x, y, size, 0, Math.PI * 2);
                ctx.fillStyle = this.color;
                ctx.fill();
            }
        }

        // Create more particles
        const particles = Array.from({ length: 200 }, () => new Particle());

        // Animation loop
        const animate = () => {
            ctx.clearRect(0, 0, canvas.width, canvas.height);

            // Update and draw particles
            particles.forEach(particle => {
                particle.update();
                particle.draw();
            });

            // Draw connections
            particles.forEach((p1, i) => {
                particles.slice(i + 1).forEach(p2 => {
                    const dx = p1.x - p2.x;
                    const dy = p1.y - p2.y;
                    const dz = p1.z - p2.z;
                    const distance = Math.sqrt(dx * dx + dy * dy + dz * dz);

                    if (distance < 200) {
                        const scale1 = 1000 / (1000 + p1.z);
                        const scale2 = 1000 / (1000 + p2.z);
                        const x1 = (p1.x - canvas.width / 2) * scale1 + canvas.width / 2;
                        const y1 = (p1.y - canvas.height / 2) * scale1 + canvas.height / 2;
                        const x2 = (p2.x - canvas.width / 2) * scale2 + canvas.width / 2;
                        const y2 = (p2.y - canvas.height / 2) * scale2 + canvas.height / 2;

                        ctx.beginPath();
                        ctx.moveTo(x1, y1);
                        ctx.lineTo(x2, y2);
                        ctx.strokeStyle = `hsla(180, 100%, 50%, ${0.3 * (1 - distance / 200)})`;
                        ctx.lineWidth = 1;
                        ctx.stroke();
                    }
                });
            });

            animationFrameId = requestAnimationFrame(animate);
        };

        animate();

        // Cleanup
        return () => {
            window.removeEventListener('resize', resizeCanvas);
            cancelAnimationFrame(animationFrameId);
        };
    }, []);

    return (
        <div className="background-container">
            <canvas ref={canvasRef} className="background-canvas" />
            <div className="background-overlay" />
        </div>
    );
};

export default Background;
